package com.fr.bi.stable.io.io.read;import com.fr.bi.stable.constant.CubeConstant;import com.fr.bi.stable.io.newio.NIOReader;/** * Created by Hiram on 2014/11/11. */public class StringIndexReadMappedList implements NIOReader<String> {    private final NIOReader<String> valueReader;    private final NIOReader<Integer> indexReader;    public StringIndexReadMappedList(NIOReader<Integer> indexReader, NIOReader<String> valueReader) {        this.valueReader = valueReader;        this.indexReader = indexReader;    }    @Override    public String get(long row) {        int index = indexReader.get(row);        if (index == CubeConstant.NULLINDEX) {            return null;        }        return valueReader.get(index);    }    @Override    public long getLastPos(long rowCount) {        return rowCount;    }    @Override    public void clear() {    }}