package com.fr.bi.cal.analyze.cal.index.loader;import com.fr.bi.cal.analyze.report.report.widget.BISummaryWidget;import com.fr.bi.conf.report.widget.field.dimension.BIDimension;import com.fr.bi.field.target.calculator.cal.FormulaCalculator;import com.fr.bi.field.target.target.BISummaryTarget;import com.fr.bi.stable.report.result.DimensionCalculator;import com.fr.bi.stable.report.result.TargetCalculator;import java.util.List;/** * Created by Hiram on 2015/1/8. */public class LoaderUtils {    static void fillRowDimension(BISummaryWidget widget, DimensionCalculator[] row, BIDimension[] rowDimension, int rowLength, BISummaryTarget bdt) {        for (int j = 0; j < rowLength; j++) {            BIDimension dimension = rowDimension[j];            if (dimension != null) {                row[j] = widget.createDimCalculator(dimension, bdt);            }        }    }    public static void classifyTarget(BISummaryTarget[] usedTargets, List<FormulaCalculator> formulaCalculateTargets) {        for (int i = 0; i < usedTargets.length; i++) {            BISummaryTarget target = usedTargets[i];            if (target == null) {                continue;            }            TargetCalculator calculator = target.createSummaryCalculator();           if(calculator instanceof FormulaCalculator) {                formulaCalculateTargets.add((FormulaCalculator) calculator);            }        }    }}