package com.fr.bi.cluster.exception;/** * Created by Hiram on 2015/2/28. */public class DistributedException extends Exception {    public DistributedException(String msg) {        super(msg);    }    public DistributedException(String msg, Throwable throwable) {        super(msg, throwable);    }    public DistributedException(Throwable throwable) {        super(throwable);    }}