package com.fr.bi.cluster.utils;import com.fr.base.FRContext;import java.io.BufferedInputStream;import java.io.File;import java.io.FileInputStream;import java.io.IOException;import java.util.Properties;/** * Created by Hiram on 2015/2/26. */public class PropertiesUtils {    public static Properties load(File file) {        Properties properties = new Properties();        try {            properties.load(new BufferedInputStream(new FileInputStream(file)));            return properties;        } catch (IOException e) {            FRContext.getLogger().error(e.getMessage(),e);        }        return null;    }}