package com.fr.bi.cluster.zookeeper;import com.fr.bi.cluster.exception.DistributedException;/** * Created by Hiram on 2015/2/27. */public class ZooKeeperException extends DistributedException {    public ZooKeeperException(String msg) {        super(msg);    }    public ZooKeeperException(String msg, Throwable throwable) {        super(msg, throwable);    }    public ZooKeeperException(Throwable throwable) {        super(throwable);    }}