package com.fr.bi.cluster.lock;import java.util.concurrent.ArrayBlockingQueue;import java.util.concurrent.BlockingQueue;/** * 这个信号类可以先通知，再等待。即先调用signal(),再调用await()可以不用等待 * Created by Hiram on 2015/2/28. */public class DelaySemaphore {    BlockingQueue blockingQueue = new ArrayBlockingQueue(1);    public void await() throws InterruptedException {        blockingQueue.take();    }    public void signal() throws InterruptedException {        blockingQueue.put(new Object());    }}