package com.fr.bi.cal.analyze.cal.index.loader;import com.fr.bi.stable.data.Table;import com.finebi.cube.api.ICubeDataLoader;import com.fr.bi.stable.report.result.DimensionCalculator;import java.util.Iterator;/** * Created by Hiram on 2015/1/15. */public class IndexIterator implements Iterator {    private final DimensionCalculator column;    private final Table tableKey;    private final ICubeDataLoader loader;    private int[] sortIndex = new int[]{2, 3, 4, 5, 6};    private Iterator iterator;    private int pos = 0;    public IndexIterator(Iterator iterator, DimensionCalculator column, Table tableKey, ICubeDataLoader loader) {        this.column = column;        this.tableKey = tableKey;        this.loader = loader;    }    @Override    public boolean hasNext() {        return pos < sortIndex.length;    }    @Override    public Object next() {        pos++;        return iterator.next();    }    @Override    public void remove() {    }}