package com.fr.bi.cal.analyze.cal.index.loader;import com.fr.bi.stable.report.key.TargetGettingKey;import com.fr.bi.stable.report.result.LightNode;/** * Created by Hiram on 2015/1/28. */public class NodeSummarizing {    private LightNode node;    protected TargetGettingKey[] targetGettingKeys;    public void setNode(LightNode node) {        this.node = node;    }    public void setTargetGettingKeys(TargetGettingKey[] targetGettingKeys) {        this.targetGettingKeys = targetGettingKeys;    }    public LightNode sum() {        sum(node);        return node;    }    protected void sum(LightNode node) {        if (node.getChildLength() == 0) {            return;        }        resetSummaryValue(node);        for (int i = 0; i < node.getChildLength(); i++) {            LightNode child = node.getChild(i);            sum(child);            for (TargetGettingKey key : targetGettingKeys) {                Number value = node.getSummaryValue(key);                if (value == null) {                    node.setSummaryValue(key, child.getSummaryValue(key));                } else {                    Number childValue = child.getSummaryValue(key);                    if (childValue != null) {                        Double sum = value.doubleValue();                        sum += childValue.doubleValue();                        node.setSummaryValue(key, sum);                    }                }            }        }    }    protected void resetSummaryValue(LightNode node) {        for (TargetGettingKey key : targetGettingKeys) {            node.setSummaryValue(key, null);        }    }}