package com.fr.bi.cal.analyze.cal.index.loader.nodeiterator;import com.fr.bi.cal.analyze.cal.sssecret.GroupConnectionValue;import com.fr.bi.cal.analyze.cal.sssecret.NodeDimensionIterator;import com.fr.bi.cal.analyze.cal.sssecret.RootDimensionGroup;/** * Created by Hiram on 2016/3/1. */public class NormalIteratorManager implements IteratorManager {	RootDimensionGroup[] rootDimensionGroups;	NodeDimensionIterator[] nodeDimensionIterators;	public NormalIteratorManager(RootDimensionGroup[] rootDimensionGroups) {		this.rootDimensionGroups = rootDimensionGroups;		nodeDimensionIterators = new NodeDimensionIterator[rootDimensionGroups.length];	}	@Override	public void moveNext() {		for (int i = 0; i < rootDimensionGroups.length; i++) {			nodeDimensionIterators[i] = rootDimensionGroups[i].moveNext();		}	}	@Override	public GroupConnectionValue[] getNextGroupConnectionValues() {		GroupConnectionValue[] ret = new GroupConnectionValue[rootDimensionGroups.length];		for (int i = 0; i < ret.length; i++) {			ret[i] = nodeDimensionIterators[i].next();		}		return ret;	}}