package com.fr.bi.cal.analyze.cal.multithread;import com.fr.bi.cal.analyze.cal.index.loader.MergerInfo;import com.fr.bi.cal.analyze.cal.sssecret.NoneDimensionGroup;import com.fr.bi.stable.report.result.IMergerNode;/** * Created by Hiram on 2015/5/14. */public class MergeSummaryCall {	private IMergerNode node;	private NoneDimensionGroup noneDimensionGroup;	private MergerInfo mergerInfo;	public MergeSummaryCall(IMergerNode node, NoneDimensionGroup noneDimensionGroup, MergerInfo mergerInfo) {		this.node = node;		this.noneDimensionGroup = noneDimensionGroup;		this.mergerInfo = mergerInfo;	}	public void cal() throws Exception {		Number v = noneDimensionGroup.getSummaryValue(mergerInfo.getSummary());		if (v != null) {			node.setSummaryValue(mergerInfo.getTargetGettingKey(), v);		}	}}