package com.fr.bi.cal.analyze.cal.multithread;import com.fr.bi.cal.analyze.cal.index.loader.MetricGroupInfo;import com.fr.bi.cal.analyze.cal.index.loader.TargetAndKey;import com.fr.bi.cal.analyze.cal.sssecret.NoneDimensionGroup;import com.fr.bi.stable.report.result.IMergerNode;/** * Created by Hiram on 2015/5/14. */public class MergeSummaryCall implements BISingleThreadCal {	private IMergerNode node;	private NoneDimensionGroup noneDimensionGroup;	private MetricGroupInfo metricGroupInfo;	public MergeSummaryCall(IMergerNode node, NoneDimensionGroup noneDimensionGroup, MetricGroupInfo mergerInfo) {		this.node = node;		this.noneDimensionGroup = noneDimensionGroup;		this.metricGroupInfo = mergerInfo;	}	public void cal() {		for (TargetAndKey targetAndKey : metricGroupInfo.getSummaryList()){			Number v = noneDimensionGroup.getSummaryValue(targetAndKey.getCalculator());			if (v != null) {				node.setSummaryValue(targetAndKey.getTargetGettingKey(), v);			}		}	}}