package com.fr.bi.cal.analyze.cal.result;import com.fr.bi.stable.constant.BIBaseConstant;import com.fr.bi.stable.gvi.GroupValueIndex;import java.util.Comparator;/** * Created by Hiram on 2015/3/30. */public class MemNode {    public final static MemNode NULL = new MemNode();    protected GroupValueIndex index;    private Object data;    private Node parent;    private Node sibling;    private GroupValueIndex gvi;    private String showValue;    private Comparator comparator = BIBaseConstant.COMPARATOR.COMPARABLE.ASC;    public Object getData() {        return data;    }    public void setData(Object data) {        this.data = data;    }    public Node getParent() {        return parent;    }    public void setParent(Node parent) {        this.parent = parent;    }    public Node getSibling() {        return sibling;    }    public void setSibling(Node sibling) {        this.sibling = sibling;    }    public GroupValueIndex getIndex() {        return index;    }    public void setIndex(GroupValueIndex index) {        this.index = index;    }    public GroupValueIndex getGroupValueIndex() {        return gvi;    }    public void setGroupValueIndex(GroupValueIndex gvi) {        this.gvi = gvi;    }    public String getShowValue() {        return showValue;    }    public void setShowValue(String showValue) {        this.showValue = showValue;    }    public Comparator getComparator() {        return comparator;    }    public void setComparator(Comparator comparator) {        this.comparator = comparator;    }    public void release() {        gvi = null;        data = null;        showValue = null;    }}