package com.fr.bi.cal.analyze.cal.result;import com.fr.bi.stable.report.result.ISortInfo;/** * Created by Hiram on 2015/1/31. */public class SortInfo implements ISortInfo {    static final ISortInfo NULL_SORT_INFO = new SortInfo(null, 0);    private String targetName;    private int sortType;    public SortInfo(String targetName, int sortType) {        this.targetName = targetName;        this.sortType = sortType;    }    @Override    public String getTargetName() {        return targetName;    }    @Override    public int getSortType() {        return sortType;    }}