package com.fr.bi.cal.analyze.cal.result;import com.fr.bi.stable.report.result.ISortInfo;import com.fr.bi.stable.report.result.ISortInfoList;/** * Created by Hiram on 2015/1/31. */public class SortInfoList implements ISortInfoList {    ISortInfo[] sortInfoArray;    ISortInfo prioritySortInfo;    @Override    public boolean shouldSort(int deep) {        return getSortInfo(deep) != SortInfo.NULL_SORT_INFO;    }    private ISortInfo getSortInfo(int deep) {        if (prioritySortInfo != null) {            return prioritySortInfo;        }        if (sortInfoArray == null) {            return SortInfo.NULL_SORT_INFO;        }        if (deep < 0 || deep >= sortInfoArray.length) {            return SortInfo.NULL_SORT_INFO;        }        if (sortInfoArray[deep] == null) {            return SortInfo.NULL_SORT_INFO;        }        return sortInfoArray[deep];    }    @Override    public void setPrioritySortInfo(ISortInfo prioritySortInfo) {        this.prioritySortInfo = prioritySortInfo;    }    @Override    public String getTargetName(int deep) {        return getSortInfo(deep).getTargetName();    }    @Override    public int getSortType(int deep) {        return getSortInfo(deep).getSortType();    }    @Override    public void setSortInfoArray(ISortInfo[] sortInfoArray) {        this.sortInfoArray = sortInfoArray;    }}