package com.fr.bi.cal.analyze.cal.sssecret;import com.fr.bi.field.dimension.calculator.CombinationDateDimensionCalculator;import com.fr.bi.field.dimension.calculator.CombinationDimensionCalculator;import com.fr.bi.stable.data.Table;import com.finebi.cube.api.ICubeDataLoader;import com.fr.bi.stable.gvi.GroupValueIndex;import com.fr.bi.stable.report.result.DimensionCalculator;/** * Created by Hiram on 2015/12/9. */public class DereplicationNoneDimenstionGroup extends NoneDimensionGroup {	public DereplicationNoneDimenstionGroup(Table tableKey, GroupValueIndex gvi, ICubeDataLoader loader) {		super(tableKey, gvi, loader);	}	public DereplicationNoneDimenstionGroup(NoneDimensionGroup noneDimensionGroup) {		super(noneDimensionGroup.tableKey, noneDimensionGroup.getRoot().getGroupValueIndex(), noneDimensionGroup.loader);	}	public static NoneDimensionGroup createDimensionGroup(final Table tableKey, final GroupValueIndex gvi, final ICubeDataLoader loader) {		return new DereplicationNoneDimenstionGroup(tableKey, gvi, loader);	}	public ISingleDimensionGroup createSingleDimensionGroup(DimensionCalculator[] pck, int[] pckindex, DimensionCalculator ck, Object[] data, int ckIndex, boolean useRealData) {		if (ck instanceof CombinationDimensionCalculator || ck instanceof CombinationDateDimensionCalculator) {			return ReverseSingleDimensionGroup.createDimensionGroup(tableKey, pck, pckindex, ck, data, ckIndex, node.getGroupValueIndex(), loader, useRealData);		}		return DereplicationSingleDimensionGroup.createDimensionGroup(tableKey, pck, pckindex, ck, data, ckIndex, node.getGroupValueIndex(), loader, useRealData);	}}