package com.fr.bi.cal.analyze.cal.sssecret.sort;import com.finebi.cube.api.ICubeValueEntryGetter;import com.fr.bi.cal.analyze.cal.sssecret.ISingleDimensionGroup;import com.fr.bi.cal.analyze.cal.sssecret.NoneDimensionGroup;import com.fr.bi.cal.analyze.cal.sssecret.SingleDimensionGroup;import com.fr.bi.stable.report.result.DimensionCalculator;/** * Created by Hiram on 2015/1/16. */public class SortedNoneDimensionGroup extends NoneDimensionGroup {    private SortedNode sortedNode;    private ISingleDimensionGroup cache;    public SortedNoneDimensionGroup(NoneDimensionGroup dimensionGroup) {        super(dimensionGroup.getTableKey(), dimensionGroup.getRoot().getGroupValueIndex(), dimensionGroup.getLoader());    }    @Override    public ISingleDimensionGroup createSingleDimensionGroup(DimensionCalculator[] pck, DimensionCalculator ck, Object[] data, int ckIndex, ICubeValueEntryGetter getter, boolean useRealData) {        if (cache == null) {            cache = SingleDimensionGroup.createSortDimensionGroup(tableKey, pck, ck, data, ckIndex, getter, node.getGroupValueIndex(), getLoader(), sortedNode, useRealData);        }        return cache;    }    public void setSortedNode(SortedNode sortedNode) {        this.sortedNode = sortedNode;    }}